	<fieldset id="timestampdiv" class="hide-if-js">
	<legend class="screen-reader-text"><?php _e( 'Date and time' ); ?></legend>
	<?php touch_time( ( $action === 'edit' ), 1 ); ?>
	</fieldset>
</div><?php // /misc-pub-section ?>
<?php endif; ?>

<?php
/**
