					$param_line .= '<option value="' . $sidebar["id"] . '"' . $selected . '>' . $sidebar_name . '</option>';
				}
				$param_line .= '</select>';
			} else {
				$param_line .= do_shortcode_param_settings_field( $param['type'], $param, $param_value );
